const { createHash } = require('crypto');
const countries = require('i18n-iso-countries');

function generateIPv4Address(seedString) {
  // Create a hash of the seed string using SHA-256
  const hash = createHash('sha256').update(seedString).digest();
console.log(hash,"131");

  const octet1 = 0;
  const octet2 = hash[1];
  const octet3 = hash[2];
  const octet4 = hash[3];

  // Return the IPv4 address as a string
  return String(octet2)+"."+String(octet1)+"."+String(octet3)+"."+String(octet4);
}

function selectRandomCountry(seedString) {
  // Create a hash of the seed string using SHA-256
  const hash = createHash('sha256').update(seedString).digest();

  // Extract a number from the hash and use it to select a random country code
  const countryCodes = Object.keys(countries.getAlpha2Codes());
  const randomIndex = hash.readUInt32BE(0) % countryCodes.length;
  const randomCountryCode = countryCodes[randomIndex];

  // Return the random country name
  return countries.getName(randomCountryCode, 'en');
}

